mapper.select:
SELECT * FROM ( SELECT g.*, u.U_NICKNAME AS gNickname, u.U_STAR AS gStar, ROW_NUMBER() OVER (ORDER BY
g.G_CREATED_DATE DESC g.G_VIEW DESC g.G_LIKE DESC g.G_NO DESC
) AS rn FROM GENERAL_TBL g JOIN USER_TBL u ON g.G_WRITER_EMAIL = u.U_EMAIL WHERE g.G_TITLE LIKE '%' || #{searchQuery} || '%' ) WHERE rn BETWEEN #{offset + 1} AND #{offset + limit}

SELECT * FROM ( SELECT g.*, u.U_NICKNAME AS gNickname, u.U_STAR AS gStar, ROW_NUMBER() OVER (ORDER BY
g.G_CREATED_DATE DESC g.G_VIEW DESC g.G_LIKE DESC g.G_NO DESC
) AS rn FROM GENERAL_TBL g JOIN USER_TBL u ON g.G_WRITER_EMAIL = u.U_EMAIL ) WHERE rn BETWEEN #{offset + 1} AND #{offset + limit}

mapper.update:
UPDATE GENERAL_TBL
SET G_VIEW = G_VIEW + 1
WHERE G_NO = #{gNo}

mapper.select:
SELECT * FROM GENERAL_COMMENT_TBL
WHERE GC_G_NO = #{gNo}
ORDER BY GC_CREATED_DATE DESC

mapper.update:
UPDATE GENERAL_COMMENT_TBL
SET GC_CONTENT = #{gcContent}
WHERE GC_NO = #{gcNo}

mapper.select:
SELECT * FROM GENERAL_COMMENT_TBL
WHERE GC_G_NO = #{generalNo}
ORDER BY GC_CREATED_DATE DESC

mapper.update:
UPDATE GENERAL_TBL
SET G_LIKE = #{likeCount}
WHERE G_NO = #{gNo}

mapper.select:
SELECT COUNT(GC_NO) AS gcCount
FROM GENERAL_COMMENT_TBL
WHERE GC_G_NO = #{gNo}
